package kr.or.yi.java_study_01.ch04;

public class Rectangle {
	int width;
	int height;

	public Rectangle() {
		// TODO Auto-generated constructor stub
	}

	public Rectangle(int width, int height) {
		this.width = width;
		this.height = height;
	}

	public int getArea() {
		return width * height;
	}
}
